mapper.delete:
DELETE
FROM feed
WHERE feedID = #{feedID} AND userID = #{userID}

mapper.update:
UPDATE feed
SET likeCnt = likeCnt+1 
WHERE feedID = #{feedID};

UPDATE feed
SET likeCnt = likeCnt-1 
WHERE feedID = #{feedID};

mapper.select:
SELECT *
FROM feed
WHERE userID = #{userID}
ORDER BY date DESC;

mapper.insert:
INSERT
INTO likefeed(
	userID,
	feedID
)
VALUES (
	#{userID},
	#{feedID}
)

mapper.delete:
DELETE
FROM likefeed
WHERE userID = #{userID} AND feedID = #{feedID}
